advanced_usage.command:
pm.sendRequest({
  url: 'https://auth.example.com/oauth/token',
  method: 'POST',
  body: {
    mode: 'urlencoded',
    urlencoded: [
      {key: 'grant_type', value: 'client_credentials'},
      {key: 'client_id', value: pm.variables.get('client_id')},
      {key: 'client_secret', value: pm.variables.get('client_secret')}
    ]
  }
}, (err, res) => {
  if (res) {
    pm.variables.set('access_token', res.json().access_token);
  }
});

const schema = {
  "type": "object",
  "properties": {
    "id": {"type": "number"},
    "name": {"type": "string"},
    "email": {"type": "string", "format": "email"}
  },
  "required": ["id", "name", "email"]
};

pm.test("Schema validation", function() {
  const responseJson = pm.response.json();
  pm.expect(tv4.validate(responseJson, schema)).to.be.true;
});
